mapper.select:

	SELECT user_id, user_name, user_pw, start_date
	FROM user_tb
	WHERE user_name=#{name}
		AND user_pw=#{pw};

SET @year := (select date_format(now(),'%y')); SET @month := (select date_format(now(),'%c')); SET @day := (select date_format(now(),'%d'));
SET @realstartdate := ( SELECT (CASE WHEN start_date > @day THEN date_format(concat(@year,"-", (((@month-1)+11)%12)+1,"-", start_date),'%y-%c-%d') WHEN start_date <=@day THEN date_format(concat(@year,"-", @month,"-", start_date),'%y-%c-%d') END) FROM user_tb );
SELECT u.user_id, u.user_name, u.user_pw, u.start_date, m.money_id, m.price, m.category, m.use_date FROM user_tb u, money_tb m WHERE 1=1 and u.user_id=m.user_id and @realstartdate <=use_date ORDER BY use_date DESC;
