mapper.select:
SET @year := (select date_format(now(),'%y')); SET @month := (select date_format(now(),'%c')); SET @day := (select date_format(now(),'%d'));
SET @realstartdate := ( SELECT (CASE WHEN cast(start_date as unsigned) > @day THEN date_format(concat(@year,"-", (((@month-1)+11)%12)+1,"-", start_date),'%y-%c-%d') WHEN cast(start_date as unsigned) <=@day THEN date_format(concat(@year,"-", @month,"-", start_date),'%y-%c-%d') END) FROM user_tb WHERE user_id=#{user_id} );
SELECT u.user_id, u.user_name, u.user_pw, u.start_date, m.money_id, m.price, m.category, m.use_date FROM user_tb u, money_tb m WHERE 1=1 and u.user_id=m.user_id and @realstartdate <=use_date and u.user_id=#{user_id} ORDER BY use_date DESC;

 
	
		set @year := (select date_format(now(),'%y'));
		set @month := (select date_format(now(),'%c'));
		set @day := (select date_format(now(),'%d')); 
			 
		set @realstartdate := 
			(
			SELECT
				(CASE
					WHEN cast(start_date as unsigned) > @day THEN  date_format(concat(@year,"-", (((@month-1)+11)%12)+1,"-", start_date),'%y-%c-%d')
			        WHEN cast(start_date as unsigned) <=@day THEN  date_format(concat(@year,"-", @month,"-", start_date),'%y-%c-%d')
			        END)
			FROM user_tb 
			WHERE user_id=#{user_id}
			);
			
		set @tmpdailyTotal :=(select sum(price) from money_tb where user_id=#{user_id} and use_date=date_format(concat(@year,"-", @month,"-", @day),'%y-%c-%d'));
		
		set @dailyTotal := (case when @tmpdailyTotal is null THEN 0 ELSE @tmpdailyTotal END);     
		
		select 
			u.user_id
			,user_name
		    ,start_date
		    ,sum(price) monthly_use
		    ,@dailyTotal daily_use
		from user_tb u, money_tb m
		where 1=1
			and u.user_id=m.user_id
			and u.user_id=#{user_id}
		    and @realstartdate <=use_date 
		group by u.user_id;

